// Helper functions to find relationships
export const getTopicsForWorkRole = (workRoleId) => {
  const workRole = WORK_ROLES.find(wr => wr.id === workRoleId);
  if (!workRole) return [];
  return TOPICS.filter(topic => workRole.topicIds.includes(topic.id));
};

export const getWorkRolesForTopic = (topicId) => {
  const topic = TOPICS.find(tp => tp.id === topicId);
  if (!topic) return [];
  return WORK_ROLES.filter(workRole => topic.workRoleIds.includes(workRole.id));
};

